
#include <iostream>
#include <climits>
#include <limits.h>
#include <cstdlib>
using namespace std;



int main()
{
	char number[] = "";
	
	unsigned short indx = 12;
  unsigned long gprod = 1, cprod = 1;
  for(int i = 0; i < 13; i++) gprod *= (number[i] - 48);
  while(indx < 1000) {
    indx++;
    if((number[indx] - 48) == 0) {
      indx += 13;
      if(indx >= 1000) continue;
      cprod = 1;
      for(int i = indx - 12; i <= indx; i++) cprod *= (number[i] - 48);
    } else {
      if((number[indx-13] - 48) != 0) cprod /= (number[indx-13] - 48);
      cprod *= (number[indx] - 48);
    }
    if(cprod > gprod) gprod = cprod;
  }
  std::cout << gprod << std::endl;
	

}


